mdq:check.code:
if(Attributes-AttributesDefinitions>0) {
  status <- "FAILURE"
  message <- paste0(Attributes-AttributesDefinitions," attribute(s) don't have a definition")
} else {
  status <- "SUCCESS"
  message <- paste0("All ",Attributes," attributes have a definition")
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
