mdq:check.code:
  if(KeywordPresence==0) {
    status <- "FAILURE"
    message <-"No Keywords are specified, it is not possible to check for keywords types."
  } else if (KeywordType-KeywordPresence<=0) {
    status <- "WARNING"
    message <-paste0(KeywordPresence-KeywordType," keywords (out of ",KeywordPresence," Keywords) don't have a type specified")
  } else {
    status <- "SUCCESS"
    message <-paste0("All ",KeywordPresence," keywords have a type specified")
  }
mdq_result <- list(status = status,
                     output = list(list(value = message)))
