mdq:check.code:
# Check that <keyword> section(s) are presents for the dataset.
if(KeywordPresence==0) {
  status <- "WARNING"
  message <- "Keywords are missing."
} else if (KeywordDef!=0) {
  status <- "WARNING"
  message <- paste0(KeywordPresence," Keywords are present but ",KeywordDef," group(s) of keywords is/are not controlled (from a vocabulary).")
} else {
  status <- "SUCCESS"
  message <- paste0(KeywordPresence," keywords are present and are controlled (from a vocabulary).")
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
