mdq:check.code:
if(Attributes-AttributeScale>0) {
  status <- "WARNING"
  message <-paste0(Attributes-AttributeScale," attribute(s) (out of ",Attributes," attributes) don't have a measurement scale")
} else {
  status <- "SUCCESS"
  message <-paste0("All ",Attributes," attributes have a measurement scale")
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
