mdq:check.code:
 library(ragtop)
  if (entitiesPresent==0){
    status = "FAILURE"
    message <-"No entities were found so it is impossible to detect any checksum."
  }else{
  entityCount = 0
  missing = c()

  # If only a single value is returned (vs type "list"), then convert to a list
  # for easier processing
  if(length(checksums)==1){
    checksums = list(checksums)
  }

  if(length(checksumAlgorithms)==1){
    checksumAlgorithms = list(checksumAlgorithms)
  }

  if(length(entityNames)==1){
    entityNames = list(entityNames)
  }

  if (length(checksums)==0){
  message = paste0("All the ",entitiesPresent," entities are missing a checksum value or algorithm")
  status="FAILURE"
  }else{
    for (i in 1:length(checksums)){
    entityCount = entityCount+1
    sum = checksums[i]
    algorithm = checksumAlgorithms[i]
    name = entityNames[i]
    if(is.null(name)){
      name=i
    }
      # No units for this attribute was specified
    if(is.blank(sum)){
    missing=append(missing,name)
    } else if (is.blank(algorithm)){
    missing=append(missing,name)
    }
  }

  if(length(missing) == 1){
    message = paste0("This 1 entity (out of ",entitiesCount, ") is missing a checksum value or algorithm: ", missing[1])
    status = "FAILURE"

  }else if(length(missing) > 1 & length(missing) < 5){
  message = paste0("These ",length(missing)," entities (out of ",entityCount,") are missing a checksum value or algorithm: ", missing)
  status="FAILURE"
  }else if (length(missing) > 5){
  message = paste0("These ",length(missing)," entities (out of ",entityCount,") are missing a checksum value or algorithm: ", missing[1:5]," and ",length(missing)-5," others")
  status="FAILURE"
  }else{
    message = "All entities have checksums and checksum algorithms specified."
    status = "SUCCESS"
  }
  }
}
  mdq_result <- list(status = status,
                         output = list(list(value = message)))
