mdq:check.code:
if(entityType!=entityName) {
  status <- "FAILURE"
  message <-paste0(entityType-entityName," entities don't have a description")
} else {
  status <- "SUCCESS"
  message <-paste0("All ",entityType," entities have a description")
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
