mdq:check.code:
if(entityType!=entityFormat) {
  status <- "FAILURE"
  message <-paste0(entityType-entityName," entities don't have a format specified")
} else {
  status <- "SUCCESS"
  message <-paste0("All ",entityType," entities have a format")
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
