mdq:check.code:
library(ragtop)
if(length(EntityIDType)==0) {
  status <- "WARNING"
  message <- "No entity ID type were found"
}else if (is.blank(EntityIDType)){
  status <- "WARNING"
  message <- "The entity ID type is blank"
} else {
  status <- "SUCCESS"
  message <-paste0("The Entity ID type ",EntityIDType, " was found.")
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
