mdq:check.code:
library(metadig)
if (length(Title) <= 0) {
  failure("No title were found.")
} else {
wordcount=length(unlist(strsplit(Title, "\\s+", perl=T)))
if (wordcount<6){
  failure(paste0("The title is to short (",wordcount,"words), it is recommended to be at minimum 6 words long"))
}
else if (wordcount>20){
  failure("The title is to long, it should be at maximum 20 words long")
}
else success("The title is present and has correct number of words")
}
