mdq:check.code:
if(length(Distributioncontact)==0){
  status <- "WARNING"
  message <- "There is no distribution contacts."
} else {
  status <- "SUCCESS"
  message <- paste0(length(Distributioncontact[!duplicated(Distributioncontact)])," different distribution contacts were found: ", paste(Distributioncontact[!duplicated(Distributioncontact)],collapse='/'))
}
mdq_result <- list(status = status,
                   output = list(list(value = message)))
