mdq:check.code:
if(Attributes-storageType>0) {
  status <- "WARNING"
  message <-paste0(Attributes-storageType," attribute(s) don't have a storage type")
} else {
  status <- "SUCCESS"
  message <-paste0("All ",Attributes," attributes have a storage type")
}
  mdq_result <- list(status = status,
                   output = list(list(value = message)))
